$(document).ready(function () {
    $("#btn-mpesa-bal").on('click', function (e) {
        e.preventDefault();
        console.log("Fetching balance");
        console.log("URL = " + fetch_mpesa_balance_url);
        $(".bal-mpesa-trx").toggle();

        $.ajax({
            url: fetch_mpesa_balance_url,
            type: 'GET',
            success: function (obj) {
                console.log("RESULT: " + obj);
                if (obj["status"] == "00") {
                    $(".bal-mpesa-trx").html("The balance has been fetched");
                } else if (obj["status"] == "01") {
                    toastr["error"](obj["message"], "Sorry!", { closeButton: true, progressBar: true, timeOut: 5000 });
                }
            }
        });
    });
});